import React, { useState } from 'react';
import { PassDesign } from '../types';
import { Palette, Type, Grid, Settings, ChevronDown, ChevronUp } from 'lucide-react';
import { Image as ImageIcon } from 'lucide-react';

interface DesignEditorProps {
  design: PassDesign;
  onDesignChange: (design: PassDesign) => void;
}

// Helper to create stable, accessible ids from labels
function labelToId(label: string): string {
  return label.toLowerCase().replace(/[^a-z0-9]+/g, '-');
}

interface ColorPickerProps {
  label: string;
  value: string;
  onChange: (color: string) => void;
  description?: string;
  idBase?: string;
}

const ColorPicker: React.FC<ColorPickerProps> = ({ label, value, onChange, description, idBase }) => {
  const base = idBase || labelToId(label);
  const colorId = `${base}-color`;
  const textId = `${base}-text`;

  return (
    <div className="space-y-2">
      <div className="flex items-center justify-between">
        <label htmlFor={textId} className="text-sm font-medium text-gray-700">{label}</label>
        <span className="text-xs text-gray-500">{value}</span>
      </div>
      <div className="flex items-center space-x-3">
        <input
          id={colorId}
          name={colorId}
          type="color"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          className="w-10 h-10 rounded-lg border-2 border-gray-200 cursor-pointer hover:border-gray-300 transition-colors"
          aria-label={`${label} color`}
        />
        <input
          id={textId}
          name={textId}
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          className="flex-1 px-3 py-2 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
          placeholder="#000000"
          aria-label={`${label} value`}
        />
      </div>
      {description && (
        <p className="text-xs text-gray-500">{description}</p>
      )}
    </div>
  );
};

interface TextInputProps {
  label: string;
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  maxLength?: number;
  id?: string;
}

const TextInput: React.FC<TextInputProps> = ({ label, value, onChange, placeholder, maxLength, id }) => {
  const inputId = id || labelToId(label);
  return (
    <div className="space-y-2">
      <label htmlFor={inputId} className="text-sm font-medium text-gray-700">{label}</label>
      <input
        id={inputId}
        name={inputId}
        type="text"
        value={value}
        onChange={(e) => onChange(e.target.value)}
        placeholder={placeholder}
        maxLength={maxLength}
        className="w-full px-3 py-2 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
      />
    </div>
  );
};

interface NumberInputProps {
  label: string;
  value: number;
  onChange: (value: number) => void;
  min?: number;
  max?: number;
  step?: number;
  unit?: string;
  id?: string;
}

const NumberInput: React.FC<NumberInputProps> = ({ label, value, onChange, min, max, step = 1, unit = '', id }) => {
  const inputId = id || labelToId(label);
  return (
    <div className="space-y-2">
      <label htmlFor={inputId} className="text-sm font-medium text-gray-700">{label}</label>
      <div className="relative">
        <input
          id={inputId}
          name={inputId}
          type="number"
          value={value}
          onChange={(e) => {
            const newValue = Number(e.target.value);
            if (!isNaN(newValue)) {
              onChange(newValue);
            }
          }}
          min={min}
          max={max}
          step={step}
          className="w-full px-3 py-2 pr-8 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
        />
        {unit && (
          <span className="absolute right-3 top-1/2 transform -translate-y-1/2 text-xs text-gray-500">
            {unit}
          </span>
        )}
      </div>
    </div>
  );
};

interface SectionHeaderProps {
  title: string;
  icon: React.ReactNode;
  isExpanded: boolean;
  onToggle: () => void;
}

const SectionHeader: React.FC<SectionHeaderProps> = ({ title, icon, isExpanded, onToggle }) => (
  <button
    onClick={onToggle}
    className="w-full flex items-center justify-between p-3 text-left hover:bg-gray-50 rounded-lg transition-colors"
    aria-expanded={isExpanded}
  >
    <div className="flex items-center space-x-2">
      {icon}
      <span className="font-medium text-gray-900">{title}</span>
    </div>
    {isExpanded ? (
      <ChevronUp className="w-4 h-4 text-gray-500" />
    ) : (
      <ChevronDown className="w-4 h-4 text-gray-500" />
    )}
  </button>
);

const DesignEditor: React.FC<DesignEditorProps> = ({ design, onDesignChange }) => {
  const [expandedSections, setExpandedSections] = useState({
    colors: true,
    content: true,
    stamps: true,
    images: true,
    layout: false
  });

  const updateDesign = (updates: Partial<PassDesign>) => {
    onDesignChange({ ...design, ...updates });
  };

  const toggleSection = (section: keyof typeof expandedSections) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };
  return (
    <div className="h-full flex flex-col bg-white">
      {/* Header */}
      <div className="p-4 border-b border-gray-200 bg-gray-50">
        <h2 className="text-lg font-semibold text-gray-900">Design Editor</h2>
        <p className="text-sm text-gray-600">Customize your Apple Wallet pass</p>
      </div>

      {/* Editor Content */}
      <div className="flex-1 overflow-y-auto">
        <div className="p-4 space-y-4">
          {/* Colors Section */}
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <SectionHeader
              title="Colors"
              icon={<Palette className="w-4 h-4 text-blue-600" />}
              isExpanded={expandedSections.colors}
              onToggle={() => toggleSection('colors')}
            />
            {expandedSections.colors && (
              <div className="p-4 space-y-4 bg-gray-50">
                <ColorPicker
                  label="Background Color"
                  value={design.backgroundColor}
                  onChange={(color) => updateDesign({ backgroundColor: color })}
                  description="Main pass background color"
                  idBase="background-color"
                />
                <ColorPicker
                  label="Text Color"
                  value={design.foregroundColor}
                  onChange={(color) => updateDesign({ foregroundColor: color })}
                  description="Primary text color"
                  idBase="text-color"
                />
                <ColorPicker
                  label="Label Color"
                  value={design.labelColor}
                  onChange={(color) => updateDesign({ labelColor: color })}
                  description="Field label color"
                  idBase="label-color"
                />
                <ColorPicker
                  label="Strip Background"
                  value={design.stripBackgroundColor}
                  onChange={(color) => updateDesign({ stripBackgroundColor: color })}
                  description="Stamp area background"
                  idBase="strip-background"
                />
              </div>
            )}
          </div>

          {/* Content Section */}
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <SectionHeader
              title="Content"
              icon={<Type className="w-4 h-4 text-green-600" />}
              isExpanded={expandedSections.content}
              onToggle={() => toggleSection('content')}
            />
            {expandedSections.content && (
              <div className="p-4 space-y-4 bg-gray-50">
                {/* Logo Image Upload */}
                <div className="space-y-2">
                  <label className="text-sm font-medium text-gray-700">Logo (top-left)</label>
                  <div className="flex items-start space-x-3">
                    <div className="w-12 h-12 bg-white rounded border flex items-center justify-center overflow-hidden">
                      {design.logoImage ? (
                        <img src={design.logoImage} alt="Logo" className="w-full h-full object-contain" />
                      ) : (
                        <span className="text-xs text-gray-400">Logo</span>
                      )}
                    </div>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (!file) return;
                        const reader = new FileReader();
                        reader.onload = (ev) => {
                          const logoImage = String(ev.target?.result || '');

                          // Create an image to get dimensions
                          const img = new Image();
                          img.onload = () => {
                            const aspectRatio = img.width / img.height;
                            const format = aspectRatio > 1.5 ? 'wide' : 'square';
                            const hideLogoText = format === 'wide';

                            updateDesign({ 
                              logoImage,
                              logoFormat: format,
                              hideLogoText
                            });
                          };
                          img.src = logoImage;
                        };
                        reader.readAsDataURL(file);
                      }}
                      className="flex-1 text-sm"
                    />
                  </div>
                  <div className="space-y-2">
                    <p className="text-xs text-gray-500">
                      <strong>Dynamic sizing:</strong> Square images (1:1) â†’ 50Ã—50px with logo text. 
                      Wide images (>1.5:1) â†’ 160Ã—50px, logo text hidden.
                    </p>
                    {design.logoImage && (
                      <div className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                        design.logoFormat === 'wide' 
                          ? 'bg-blue-100 text-blue-800' 
                          : 'bg-green-100 text-green-800'
                      }`}>
                        {design.logoFormat === 'wide' 
                          ? `ðŸ“ Wide format (160Ã—50px) - Logo text hidden` 
                          : `â¬œ Square format (50Ã—50px) - Logo text visible`
                        }
                      </div>
                    )}
                  </div>
                </div>

                <TextInput
                  label="Logo Text"
                  value={design.logoText}
                  onChange={(value) => updateDesign({ logoText: value })}
                  placeholder="COFFEE REWARDS"
                  maxLength={20}
                  id="logo-text"
                />
                <div className="space-y-3">
                  <h4 className="text-sm font-medium text-gray-700 border-b border-gray-200 pb-1">Expiration</h4>
                  <div className="grid grid-cols-2 gap-3">
                    <div className="space-y-2">
                      <label htmlFor="expiration-date" className="text-sm font-medium text-gray-700">Expiration Date</label>
                      <input
                        id="expiration-date"
                        name="expiration-date"
                        type="date"
                        value={(design.expirationDate || '').slice(0, 10)}
                        onChange={(e) => updateDesign({ expirationDate: e.target.value })}
                        className="w-full px-3 py-2 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                      />
                      <p className="text-xs text-gray-500">Shown as EXP in the header and embedded in pass.json</p>
                    </div>
                  </div>
                </div>

                <div className="space-y-3">
                  <h4 className="text-sm font-medium text-gray-700 border-b border-gray-200 pb-1">Header Field</h4>
                  <div className="grid grid-cols-2 gap-3">
                    <TextInput
                      label="Label"
                      value={design.headerField.label}
                      onChange={(value) => updateDesign({ 
                        headerField: { ...design.headerField, label: value }
                      })}
                      maxLength={15}
                      id="header-label"
                    />
                    <TextInput
                      label="Value"
                      value={design.headerField.value}
                      onChange={(value) => updateDesign({ 
                        headerField: { ...design.headerField, value: value }
                      })}
                      maxLength={20}
                      id="header-value"
                    />
                  </div>
                </div>

                <div className="space-y-3">
                  <h4 className="text-sm font-medium text-gray-700 border-b border-gray-200 pb-1">Secondary Fields</h4>
                  {design.secondaryFields.map((field, index) => (
                    <div key={index} className="p-3 bg-white rounded-lg border border-gray-100 space-y-3">
                      <div className="flex items-center justify-between">
                        <span className="text-xs font-medium text-gray-600">Field {index + 1}</span>
                      </div>
                      <div className="grid grid-cols-2 gap-3">
                        <TextInput
                          label="Label"
                          value={field.label}
                          onChange={(value) => {
                            const newFields = [...design.secondaryFields];
                            newFields[index] = { ...field, label: value };
                            updateDesign({ secondaryFields: newFields });
                          }}
                          maxLength={15}
                          id={`secondary-label-${index}`}
                        />
                        <TextInput
                          label="Value"
                          value={field.value}
                          onChange={(value) => {
                            const newFields = [...design.secondaryFields];
                            newFields[index] = { ...field, value: value };
                            updateDesign({ secondaryFields: newFields });
                          }}
                          maxLength={25}
                          id={`secondary-value-${index}`}
                        />
                      </div>
                    </div>
                  ))}
                </div>

              </div>
            )}
          </div>

          {/* Stamps Section */}
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <SectionHeader
              title="Stamps"
              icon={<Grid className="w-4 h-4 text-purple-600" />}
              isExpanded={expandedSections.stamps}
              onToggle={() => toggleSection('stamps')}
            />
            {expandedSections.stamps && (
              <div className="p-4 space-y-4 bg-gray-50">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700">Total Stamps</label>
                    <select
                      value={design.totalStamps}
                      onChange={(e) => {
                        const newTotal = parseInt(e.target.value);
                        updateDesign({ 
                          totalStamps: newTotal,
                          stampsEarned: Math.min(design.stampsEarned, newTotal)
                        });
                      }}
                      className="w-full px-3 py-2 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                    >
                      {Array.from({ length: 30 }, (_, i) => (
                        <option key={i + 1} value={i + 1}>
                          {i + 1} stamps
                        </option>
                      ))}
                    </select>
                    <p className="text-xs text-gray-500">Total number of stamps needed</p>
                  </div>
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700">Stamps Earned</label>
                    <select
                      value={design.stampsEarned}
                      onChange={(e) => updateDesign({ 
                        stampsEarned: parseInt(e.target.value) 
                      })}
                      className="w-full px-3 py-2 text-sm border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                    >
                      {Array.from({ length: design.totalStamps + 1 }, (_, i) => (
                        <option key={i} value={i}>
                          {i} stamps
                        </option>
                      ))}
                    </select>
                    <p className="text-xs text-gray-500">Cannot exceed total stamps</p>
                  </div>
                </div>
                <div className="p-3 bg-white rounded-lg border border-gray-100">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-600">Progress</span>
                    <span className="font-medium text-gray-900">
                      {design.stampsEarned} / {design.totalStamps} stamps
                    </span>
                  </div>
                  <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${(design.stampsEarned / design.totalStamps) * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            )}
          </div>

          {/* Images Section */}
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <SectionHeader
              title="Images"
              icon={<ImageIcon className="w-4 h-4 text-pink-600" />}
              isExpanded={expandedSections.images}
              onToggle={() => toggleSection('images')}
            />
            {expandedSections.images && (
              <div className="p-4 space-y-4 bg-gray-50">
                {/* Unredeemed Icon */}
                <div className="space-y-2">
                  <label className="text-sm font-medium text-gray-700">Stamp Icon (Unredeemed)</label>
                  <div className="flex items-start space-x-3">
                    <div className="w-12 h-12 bg-white rounded border flex items-center justify-center overflow-hidden">
                      {design.stampIconUnredeemed ? (
                        <img src={design.stampIconUnredeemed} alt="Unredeemed icon" className="w-full h-full object-contain" />
                      ) : (
                        <span className="text-xs text-gray-400">Unrdm</span>
                      )}
                    </div>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (!file) return;
                        const reader = new FileReader();
                        reader.onload = (ev) => updateDesign({ stampIconUnredeemed: String(ev.target?.result || '') });
                        reader.readAsDataURL(file);
                      }}
                      className="flex-1 text-sm"
                    />
                  </div>
                </div>

                {/* Use Same Icon Toggle */}
                <label className="inline-flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={!!design.useSameStampIcon}
                    onChange={(e) => updateDesign({ useSameStampIcon: e.target.checked })}
                    className="w-4 h-4 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 focus:ring-2"
                  />
                  <span className="text-sm text-gray-700">Use Same Icon for Redeemed</span>
                </label>

                {/* Redeemed Icon (hidden when using same) */}
                {!design.useSameStampIcon && (
                  <div className="space-y-2">
                    <label className="text-sm font-medium text-gray-700">Stamp Icon (Redeemed)</label>
                    <div className="flex items-start space-x-3">
                      <div className="w-12 h-12 bg-white rounded border flex items-center justify-center overflow-hidden">
                        {design.stampIconRedeemed ? (
                          <img src={design.stampIconRedeemed} alt="Redeemed icon" className="w-full h-full object-contain" />
                        ) : (
                          <span className="text-xs text-gray-400">Rdm</span>
                        )}
                      </div>
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => {
                          const file = e.target.files?.[0];
                          if (!file) return;
                          const reader = new FileReader();
                          reader.onload = (ev) => updateDesign({ stampIconRedeemed: String(ev.target?.result || '') });
                          reader.readAsDataURL(file);
                        }}
                        className="flex-1 text-sm"
                      />
                    </div>
                  </div>
                )}
                <p className="text-xs text-gray-500">Recommended size: 32Ã—32px or larger. PNG with transparency preferred.</p>
              </div>
            )}
          </div>

          {/* Layout Section */}
          <div className="border border-gray-200 rounded-lg overflow-hidden">
            <SectionHeader
              title="Layout"
              icon={<Settings className="w-4 h-4 text-orange-600" />}
              isExpanded={expandedSections.layout}
              onToggle={() => toggleSection('layout')}
            />
            {expandedSections.layout && (
              <div className="p-4 space-y-4 bg-gray-50">
                <div className="grid grid-cols-2 gap-4">
                  <NumberInput
                    label="Logo Size"
                    value={design.logoSize}
                    onChange={(value) => updateDesign({ logoSize: Math.max(20, Math.min(60, value)) })}
                    min={20}
                    max={60}
                    unit="pt"
                    id="logo-size"
                  />
                  <NumberInput
                    label="Header Height"
                    value={design.headerHeight}
                    onChange={(value) => updateDesign({ headerHeight: Math.max(60, Math.min(120, value)) })}
                    min={60}
                    max={120}
                    unit="pt"
                    id="header-height"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <NumberInput
                    label="Strip Height"
                    value={design.stripHeight}
                    onChange={(value) => updateDesign({ stripHeight: Math.max(100, Math.min(200, value)) })}
                    min={100}
                    max={200}
                    unit="pt"
                    id="strip-height"
                  />
                  <NumberInput
                    label="Secondary Height"
                    value={design.secondaryHeight}
                    onChange={(value) => updateDesign({ secondaryHeight: Math.max(60, Math.min(120, value)) })}
                    min={60}
                    max={120}
                    unit="pt"
                    id="secondary-height"
                  />
                </div>
                <div className="grid grid-cols-1 gap-4">
                  <NumberInput
                    label="Barcode Height"
                    value={design.barcodeHeight}
                    onChange={(value) => updateDesign({ barcodeHeight: Math.max(60, Math.min(120, value)) })}
                    min={60}
                    max={120}
                    unit="pt"
                    id="barcode-height"
                  />
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DesignEditor;
